import React, { Component } from "react";
import Stopwatch from "./Components/Stopwatch";
import Countdown from "./Components/Countdown";

class App extends Component {
  constructor(prop) {
    super();

    this.state = {
      countdown: {
        hours: 0,
        minutes: 0,
        seconds: 0,
      },
      show: {
        stopwatch: false,
        countdown: false,
      },
    };
  }

  handleZero = (counter) => {
    if (+counter < 10) {
      return `0${counter}`;
    }
    return counter;
  };

  handlePlus = (counter) => {
    let preState = this.state.countdown;
    preState[counter] = preState[counter] + 1;

    this.setState({
      countdown: preState,
    });
  };

  handleMinus = (counter) => {
    let prevState = this.state.countdown;

    if (prevState[counter] > 0) {
      prevState[counter] = prevState[counter] - 1;

      this.setState({
        countdown: prevState,
      });
    }

    return;
  };

  toggleCard = (watch) => {
    if (!this.state.show[watch]) {
    }
  };

  render() {
    return (
      <>
        <section className="main">
          <h1>⌛️ Timers ⏳</h1>
          <div className="flex nav">
            <button onClick={() => this.toggleCard("stopwatch")}>
              Show Stopwatch
            </button>
            <button onClick={() => this.toggleCard("countdown")}>
              Show Countdown
            </button>
          </div>
        </section>

        <section className="card-container flex">
          <Stopwatch />
          <Countdown
            handleZero={this.handleZero}
            counter={this.state.countdown}
            handlePlus={this.handlePlus}
            handleMinus={this.handleMinus}
          />
        </section>
      </>
    );
  }
}

export default App;
