import React, { Component } from "react";

class Countdown extends Component {
  constructor() {
    super();

    this.state = {
      timer: {
        hours: 0,
        minutes: 0,
        seconds: 0,
      },
      isTimerOn: false,
    };
  }

  useEffect = () => {
    const { isTimerOn, timer } = this.state;
    let newTimer = timer;

    if (isTimerOn) {
      if (timer.seconds > 0) {
        newTimer.seconds -= 1;
        this.setState({ timer: newTimer });
      } else if (timer.minutes > 0) {
        newTimer.minutes -= 1;
        newTimer.seconds = 59;
        this.setState({ timer: newTimer });
      } else if (timer.hours > 0) {
        newTimer.hours -= 1;
        newTimer.minutes = 59;
        newTimer.seconds = 59;
        this.setState({ timer: newTimer });
      }
    }
    return () => clearInterval(interval);
  };

  handleZero = (time) => {
    if (time < 10) {
      return `0${time}`;
    }
    return time;
  };

  handlePlus = (input) => {
    const { isTimerOn, timer } = this.state;
    let prevState = timer;

    if (!isTimerOn) {
      prevState[input] = prevState[input] + 1;

      this.setState({
        timer: prevState,
      });
    }
  };

  handleMinus = (input) => {
    const { isTimerOn, timer } = this.state;
    let prevState = timer;

    if (!isTimerOn && timer[input] > 0) {
      prevState[input] = prevState[input] - 1;

      this.setState({
        timer: prevState,
      });
    }
  };

  handleReset = () => {
    this.setState({
      timer: {
        hours: 0,
        minutes: 0,
        seconds: 0,
      },
    });
  };

  handleStart = () => {};

  render() {
    let { toggleCard } = this.props;
    let { hours, minutes, seconds } = this.state.timer;

    return (
      <>
        <div className="card">
          {/* Close Button */}
          <button
            onClick={() => {
              toggleCard("countdown");
            }}
            className="close"
          >
            x
          </button>

          <h3>Countdown</h3>

          <h2>Hours:Minutes:Seconds</h2>

          <div>
            <button
              onClick={() => this.handlePlus("hours")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlus("minutes")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlus("seconds")}
              className="arrow up"
            >
              ⬆
            </button>
          </div>

          <h3>
            {this.handleZero(hours)} : {this.handleZero(minutes)} :{" "}
            {this.handleZero(seconds)}
          </h3>

          <div>
            <button
              onClick={() => this.handleMinus("hours")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handleMinus("minutes")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handleMinus("seconds")}
              className="arrow down"
            >
              ⬇
            </button>
          </div>

          <div className="start flex">
            <button>Start</button>
          </div>
        </div>
      </>
    );
  }
}
export default Countdown;
