import React, { Component } from "react";

class Countdown extends Component {
  constructor() {
    super();

    this.state = {
      timer: 0,
      isTimerOn: false,
    };
  }

  handlePlus = (counter) => {
    const { timer, isTimerOn } = this.state;
    let newTimer = timer;

    if (!isTimerOn) {
      if (counter === "hours") {
        return (newTimer = (newTimer + 1000) * 60 * 60);
      }

      if (counter === "minutes") {
        newTimer = (newTimer + 1000) * 60;
        return newTimer;
      }

      if (counter === "seconds") {
        newTimer = newTimer + 1000;
        return newTimer;
      }
    }

    this.setState({
      timer: newTimer,
    });
  };

  // handleMinus = (counter) => {
  //   // console.log(counter, "handleMinus");
  //   const { timer, isTimerOn } = this.state;

  //   if (timer > 0) {
  //     prevState[counter] = prevState[counter] - 1;

  //     this.setState({
  //       countdown: prevState,
  //     });
  //   }

  //   return;
  // };

  render() {
    let { toggleCard } = this.props;
    return (
      <>
        <div className="card">
          {/* Close Button */}
          <button
            onClick={() => {
              toggleCard("countdown");
            }}
            className="close"
          >
            x
          </button>

          <h3>Countdown</h3>

          <h2>Hours:Minutes:Seconds</h2>

          <div>
            <button
              onClick={() => this.handlePlus("hours")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlus("minutes")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlus("seconds")}
              className="arrow up"
            >
              ⬆
            </button>
          </div>

          <h3>{this.state.timer}</h3>

          <div>
            <button
              onClick={() => this.handleMinus("hours")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handleMinus("minutes")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handleMinus("seconds")}
              className="arrow down"
            >
              ⬇
            </button>
          </div>

          <div className="start flex">
            <button>Start</button>
          </div>
        </div>
      </>
    );
  }
}
export default Countdown;
