import React, { Component } from "react";

class Countdown extends Component {
  constructor() {
    super();

    this.state = {
      timer: {
        hours: 0,
        minutes: 0,
        seconds: 0,
      },
      isTimerOn: false,
    };
  }

  handleTimer = (counter) => {
    const { timer, isTimerOn } = this.state;

    if (!isTimerOn) {
      // Plus Timer
      switch (counter) {
        case "+hours":
          return this.setState({
            timer: (timer + 1000) * 60 * 60,
          });

        case "+minutes":
          return this.setState({
            timer: (timer + 1000) * 60,
          });

        case "+seconds":
          return this.setState({
            timer: timer + 1000,
          });

        default:
          break;
      }

      // Minus Timer

      if (timer > 0) {
        switch (counter) {
          case "-hours":
            return this.setState({
              timer: (timer - 1000) / 60 / 60,
            });

          case "-minutes":
            return this.setState({
              timer: (timer - 1000) / 60,
            });

          case "-seconds":
            return this.setState({
              timer: timer - 1000,
            });
        }
      }
    }
  };

  showTime = (time) => {};

  render() {
    let { toggleCard } = this.props;
    let { hours, minutes, seconds } = this.state.timer;

    return (
      <>
        <div className="card">
          {/* Close Button */}
          <button
            onClick={() => {
              toggleCard("countdown");
            }}
            className="close"
          >
            x
          </button>

          <h3>Countdown</h3>

          <h2>Hours:Minutes:Seconds</h2>

          <div>
            <button
              onClick={() => this.handleTimer("+hours")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handleTimer("+minutes")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handleTimer("+seconds")}
              className="arrow up"
            >
              ⬆
            </button>
          </div>

          <h3>
            {hours} : {minutes} : {seconds}
          </h3>

          <div>
            <button
              onClick={() => this.handleTimer("-hours")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handleTimer("-minutes")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handleTimer("-seconds")}
              className="arrow down"
            >
              ⬇
            </button>
          </div>

          <div className="start flex">
            <button>Start</button>
          </div>
        </div>
      </>
    );
  }
}
export default Countdown;
