import React, { Component } from "react";
import Stopwatch from "./Components/Stopwatch";
import Countdown from "./Components/Countdown";

class App extends Component {
  constructor(prop) {
    super();
    this.state = {
      countdown: {
        hour: 0,
        minutes: 0,
        seconds: 0,
      },
    };
  }

  render() {
    return (
      <>
        <section className="main">
          <h1>⌛️ Timers ⏳</h1>
          {/* <div className="flex nav">
          <button>Show Stopwatch</button>
          <button>Show Countdown</button>
        </div> */}
        </section>

        <section className="card-container flex">
          <Stopwatch />
          <Countdown counter={this.state.countdown} />
        </section>
      </>
    );
  }
}

export default App;
