import React, { Component } from "react";

class Countdown extends Component {
  constructor() {
    super();

    this.state = {
      timer: 0,
      isTimerOn: false,
    };
  }

  handlePlusMinus = (counter) => {
    const { timer, isTimerOn } = this.state;

    if (!isTimerOn) {
      switch (counter) {
        case "+hours":
          return this.setState({
            timer: (timer + 1000) * 60 * 60,
          });
          break;

        case "-hours":
          return this.setState({
            timer: (timer - 1000) * 60 * 60,
          });
          break;

        case "+minutes":
          return this.setState({
            timer: (timer + 1000) * 60,
          });
          break;

        case "+minutes":
          return this.setState({
            timer: (timer + 1000) * 60,
          });
          break;

        case "+seconds":
          return this.setState({
            timer: timer + 1000,
          });
          break;

        default:
          break;
      }
    }
  };

  render() {
    let { toggleCard } = this.props;

    return (
      <>
        <div className="card">
          {/* Close Button */}
          <button
            onClick={() => {
              toggleCard("countdown");
            }}
            className="close"
          >
            x
          </button>

          <h3>Countdown</h3>

          <h2>Hours:Minutes:Seconds</h2>

          <div>
            <button
              onClick={() => this.handlePlusMinus("+hours")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlusMinus("+minutes")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlusMinus("+seconds")}
              className="arrow up"
            >
              ⬆
            </button>
          </div>

          <h3>{this.state.timer}</h3>

          <div>
            <button
              onClick={() => this.handlePlusMinus("-hours")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handlePlusMinus("-minutes")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => this.handlePlusMinus("-seconds")}
              className="arrow down"
            >
              ⬇
            </button>
          </div>

          <div className="start flex">
            <button>Start</button>
          </div>
        </div>
      </>
    );
  }
}
export default Countdown;
