import React, { Component } from "react";
import Stopwatch from "./Components/Stopwatch";
import Countdown from "./Components/Countdown";

class App extends Component {
  constructor(prop) {
    super();

    this.state = {
      show: {
        stopwatch: false,
        countdown: false,
      },

      start: {
        stopwatch: false,
        countdown: false,
      },
    };
  }

  handleStart = (watch) => {
    // console.log(watch, "handleStart");
    if (watch === "countdown") {
      let prevState = this.state.countdown;
      prevState.seconds = prevState.seconds - 1;

      if (prevState.seconds === 0) {
        prevState.seconds = 60;
      }

      this.setState({
        countdown: prevState,
      });
    }
  };

  handleReset = (watch) => {
    // console.log(watch, "handleReset");
    if (watch === "countdown") {
      this.setState({
        countdown: {
          hours: 0,
          minutes: 0,
          seconds: 0,
        },
      });
    }
  };

  handleZero = (counter) => {
    // console.log(counter, "handleZero");
    if (+counter < 10) {
      return `0${counter}`;
    }
    return counter;
  };

  handlePlus = (counter) => {
    // console.log(counter, "handlePlus");
    let prevState = this.state.countdown;
    prevState[counter] = prevState[counter] + 1;

    this.setState({
      countdown: prevState,
    });
  };

  handleMinus = (counter) => {
    // console.log(counter, "handleMinus");
    let prevState = this.state.countdown;

    if (prevState[counter] > 0) {
      prevState[counter] = prevState[counter] - 1;

      this.setState({
        countdown: prevState,
      });
    }

    return;
  };

  toggleCard = (watch) => {
    // console.log(watch, "toggleCard");
    let prevState = this.state.show;
    prevState[watch] = !prevState[watch];

    this.setState({
      show: prevState,
    });
  };

  render() {
    return (
      <>
        <section className="main">
          <h1>⌛️ Timers ⏳</h1>
          <div className=" card-container flex nav">
            {!this.state.show.stopwatch ? (
              <button onClick={() => this.toggleCard("stopwatch")}>
                Show Stopwatch
              </button>
            ) : (
              <Stopwatch
                handleStart={this.handleStart}
                toggleCard={this.toggleCard}
              />
            )}

            {!this.state.show.countdown ? (
              <button onClick={() => this.toggleCard("countdown")}>
                Show Countdown
              </button>
            ) : (
              <Countdown
                handleReset={this.handleReset}
                handleZero={this.handleZero}
                toggleCard={this.toggleCard}
                countdown={this.state.countdown}
                handlePlus={this.handlePlus}
                handleMinus={this.handleMinus}
                handleStart={this.handleStart}
              />
            )}
          </div>
        </section>
      </>
    );
  }
}

export default App;
