import React, { Component } from "react";

class Countdown extends Component {
  constructor(props) {
    super(props);

    this.state = {
      timer: 0,
    };
  }

  handlePlus = (counter) => {
    // console.log(counter, "handlePlus");
    let prevState = this.state.countdown;
    prevState[counter] = prevState[counter] + 1;

    this.setState({
      countdown: prevState,
    });
  };

  handleMinus = (counter) => {
    // console.log(counter, "handleMinus");
    let prevState = this.state.countdown;

    if (prevState[counter] > 0) {
      prevState[counter] = prevState[counter] - 1;

      this.setState({
        countdown: prevState,
      });
    }

    return;
  };

  render({ toggleCard }) {
    return (
      <>
        <div className="card">
          {/* Close Button */}
          <button
            onClick={() => {
              toggleCard("countdown");
            }}
            className="close"
          >
            x
          </button>

          <h3>Countdown</h3>

          <h2>Hours:Minutes:Seconds</h2>

          <div>
            <button
              onClick={() => this.handlePlus("hours")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlus("minutes")}
              className="arrow up"
            >
              ⬆
            </button>
            <button
              onClick={() => this.handlePlus("seconds")}
              className="arrow up"
            >
              ⬆
            </button>
          </div>

          <h3>00:00:00</h3>

          <div>
            <button onClick={() => handleMinus("hours")} className="arrow down">
              ⬇
            </button>
            <button
              onClick={() => handleMinus("minutes")}
              className="arrow down"
            >
              ⬇
            </button>
            <button
              onClick={() => handleMinus("seconds")}
              className="arrow down"
            >
              ⬇
            </button>
          </div>

          <div className="start flex">
            <button onClick={() => handleStart("countdown")}>Start</button>
          </div>
        </div>
      </>
    );
  }
}
export default Countdown;
